import mongoose from 'mongoose' ;

const Schema = mongoose.Schema ;

export const playerSchema = new Schema({

    firstName : {
        type:String,
        required:true
    },
    lastName : {
        type:String,
        required:true
    },
    email : {
        type : String ,
        //required : true
    },
    phone : {
        type : Number 
    },
    isCoach : {
        type : Boolean,
        default : false 
    },
    team : {
        type : String
    },
    speed : {
        type : Number ,
        enum : [1,2,3]
    },
    strength : {
        type : Number ,
        default : 1
    },
    endurance : {
        type : Number ,
        enum : [1,2,3]
    },
    ability : {
        type : Number ,
        enum : [1,2,3]
    },
    techniques : {
        type : Number ,
        enum : [1,2,3]
    },
    speed : {
        type : Number ,
        enum : [1,2,3]
    },
    tactical : {
       
            type : Number ,
            enum : [1,2,3]
        },
    created_date : {
        type : Date ,
        default : Date.now

    }
    
})
